# let file_path = args[2].clone();
# # Ok(Config { query, file_path })
# }
# }
# # pub fn run(config: Config) -> Result<(), Box<dyn Error>> {
# let contents = fs::read_to_string(config.file_path)?;
# # Ok(())
# }
# #[cfg(test)]
mod tests { use super::*; #[test] fn one_result() { let query = "duct"; let contents = "\
Rust:
safe, fast, productive.
Pick three."; assert_eq!(vec!["safe, fast, productive."], search(query, contents)); }
} Listing 12-15: 구현하고자 하는 search 함수를 위한 실패하는 테스트 만들기 이 테스트는 문자열 "duct"를 검색합니다. 검색하는 텍스트는 세 줄인데, 그중 한줄만이 "duct"를 가지고 있습니다 (앞의 큰 따움표 뒤에 붙은 역슬래시는 이 문자열 리터럴 내용의 앞에 새 줄 글자를 집어넣지 않도록 하라고 러스트에게 알려주는 것임을 유의하세요). search 함수가 반환하는 값은 우리가 예상하는 라인만 가지고 있을 것이라고 단언해 두었습니다. 이 테스트는 아직 컴파일도 되지 않을 것이므로 테스트를 실행시켜서 실패하는 걸 지켜볼 수는 없습니다: 아직 search 함수가 없으니까요! TDD 원칙에 따라서, Listing 12-16과 같이 항상 빈 벡터를 반환하는 search 함수 정의부를 추가하는 것으로 컴파일과 테스트가 동작하기에 딱 충분한 코드만 집어넣어 보겠습니다. 그러면 테스트는 컴파일되고, 반환된 빈 벡터가 "safe, fast, productive." 라인을 가지고 있는 벡터와 일치하지 않으므로 실패해야 합니다. Filename: src/lib.rs # use std::error::Error;
# use std::fs;
# # pub struct Config {
# pub query: String,
# pub file_path: String,
# }
# # impl Config {
# pub fn build(args: &[String]) -> Result<Config, &'static str> {
# if args.len() < 3 {
# return Err("not enough arguments");
# }
# # let query = args[1].clone();
# let file_path = args[2].clone();
# # Ok(Config { query, file_path })
# }
# }
# # pub fn run(config: Config) -> Result<(), Box<dyn Error>> {
# let contents = fs::read_to_string(config.file_path)?;
# # Ok(())
# }
# pub fn search<'a>(query: &str, contents: &'a str) -> Vec<&'a str> { vec![]
}
# # #[cfg(test)]
# mod tests {
# use super::*;
# # #[test]
# fn one_result() {
# let query = "duct";
